from .enums import ProbingState
from .charsetprober import CharSetProber


class CharSetGroupProber(CharSetProber):
    def __init__(self, lang_filter=None):
        super(CharSetGroupProber, self).__init__(lang_filter=lang_filter)
        _active_num = 0
        probers = []
        _best_guess_prober = None

    def reset(self):
        super(CharSetGroupProber, self).reset()
        _active_num = 0
        for prober in probers:
            if prober:
                prober.reset()
                prober.active = True
                _active_num += 1
        _best_guess_prober = None

    @property
    def charset_name(self):
        if not _best_guess_prober:
            get_confidence()
            if not _best_guess_prober:
                return None
        return _best_guess_prober.charset_name

    @property
    def language(self):
        if not _best_guess_prober:
            get_confidence()
            if not _best_guess_prober:
                return None
        return _best_guess_prober.language

    def feed(self, byte_str):
        for prober in probers:
            if not prober:
                continue
            if not prober.active:
                continue
            state = prober.feed(byte_str)
            if not state:
                continue
            if state == ProbingState.FOUND_IT:
                _best_guess_prober = prober
                return state
            elif state == ProbingState.NOT_ME:
                prober.active = False
                _active_num -= 1
                if _active_num <= 0:
                    _state = ProbingState.NOT_ME
                    return state
        return state

    def get_confidence(self):
        state = state
        if state == ProbingState.FOUND_IT:
            return 0.99
        elif state == ProbingState.NOT_ME:
            return 0.01
        best_conf = 0.0
        _best_guess_prober = None
        for prober in probers:
            if not prober:
                continue
            if not prober.active:
                logger.debug('%s not active', prober.charset_name)
                continue
            conf = prober.get_confidence()
            logger.debug('%s %s confidence = %s', prober.charset_name, prober.language, conf)
            if best_conf < conf:
                best_conf = conf
                _best_guess_prober = prober
        if not _best_guess_prober:
            return 0.0
        return best_conf
