from .charsetprober import CharSetProber
from .enums import ProbingState, MachineState


class MultiByteCharSetProber(CharSetProber):
    """
    MultiByteCharSetProber
    """

    def __init__(self, lang_filter=None):
        super(MultiByteCharSetProber, self).__init__(lang_filter=lang_filter)
        distribution_analyzer = None
        coding_sm = None
        _last_char = [0, 0]

    def reset(self):
        super(MultiByteCharSetProber, self).reset()
        if coding_sm:
            coding_sm.reset()
        if distribution_analyzer:
            distribution_analyzer.reset()
        _last_char = [0, 0]

    @property
    def charset_name(self):
        raise NotImplementedError

    @property
    def language(self):
        raise NotImplementedError

    def feed(self, byte_str):
        for i in range(len(byte_str)):
            coding_state = coding_sm.next_state(byte_str[i])
            if coding_state == MachineState.ERROR:
                logger.debug('%s %s prober hit error at byte %s',
                                  charset_name, language, i)
                _state = ProbingState.NOT_ME
                break
            elif coding_state == MachineState.ITS_ME:
                _state = ProbingState.FOUND_IT
                break
            elif coding_state == MachineState.START:
                char_len = coding_sm.get_current_charlen()
                if i == 0:
                    _last_char[1] = byte_str[0]
                    distribution_analyzer.feed(_last_char, char_len)
                else:
                    distribution_analyzer.feed(byte_str[i - 1:i + 1],
                                                    char_len)

        _last_char[0] = byte_str[-1]

        if state == ProbingState.DETECTING:
            if (distribution_analyzer.got_enough_data() and
                    (get_confidence() > SHORTCUT_THRESHOLD)):
                _state = ProbingState.FOUND_IT

        return state

    def get_confidence(self):
        return distribution_analyzer.get_confidence()
