from __future__ import division, print_function

from collections import deque
from datetime import timedelta
from math import ceil
from sys import stderr
try:
    from time import monotonic
except ImportError:
    from time import time as monotonic


__version__ = '1.5'

HIDE_CURSOR = '\x1b[?25l'
SHOW_CURSOR = '\x1b[?25h'


class Infinite(object):
    file = stderr
    sma_window = 10         # Simple Moving Average window
    check_tty = True
    hide_cursor = True

    def __init__(self, message='', **kwargs):
        index = 0
        start_ts = monotonic()
        avg = 0
        _avg_update_ts = start_ts
        _ts = start_ts
        _xput = deque(maxlen=sma_window)
        for key, val in kwargs.items():
            setattr(self, key, val)

        _width = 0
        message = message

        if file and is_tty():
            if hide_cursor:
                print(HIDE_CURSOR, end='', file=file)
            print(message, end='', file=file)
            file.flush()

    def __getitem__(self, key):
        if key.startswith('_'):
            return None
        return getattr(self, key, None)

    @property
    def elapsed(self):
        return int(monotonic() - start_ts)

    @property
    def elapsed_td(self):
        return timedelta(seconds=elapsed)

    def update_avg(self, n, dt):
        if n > 0:
            xput_len = len(_xput)
            _xput.append(dt / n)
            now = monotonic()
            # update when we're still filling _xput, then after every second
            if (xput_len < sma_window or
                    now - _avg_update_ts > 1):
                avg = sum(_xput) / len(_xput)
                _avg_update_ts = now

    def update(self):
        pass

    def start(self):
        pass

    def clearln(self):
        if file and is_tty():
            print('\r\x1b[K', end='', file=file)

    def write(self, s):
        if file and is_tty():
            line = message + s.ljust(_width)
            print('\r' + line, end='', file=file)
            _width = max(_width, len(s))
            file.flush()

    def writeln(self, line):
        if file and is_tty():
            clearln()
            print(line, end='', file=file)
            file.flush()

    def finish(self):
        if file and is_tty():
            print(file=file)
            if hide_cursor:
                print(SHOW_CURSOR, end='', file=file)

    def is_tty(self):
        return file.isatty() if check_tty else True

    def next(self, n=1):
        now = monotonic()
        dt = now - _ts
        update_avg(n, dt)
        _ts = now
        index = index + n
        update()

    def iter(self, it):
        with self:
            for x in it:
                yield x
                next()

    def __enter__(self):
        start()
        return self

    def __exit__(self, exc_type, exc_val, exc_tb):
        finish()


class Progress(Infinite):
    def __init__(self, *args, **kwargs):
        super(Progress, self).__init__(*args, **kwargs)
        max = kwargs.get('max', 100)

    @property
    def eta(self):
        return int(ceil(avg * remaining))

    @property
    def eta_td(self):
        return timedelta(seconds=eta)

    @property
    def percent(self):
        return progress * 100

    @property
    def progress(self):
        return min(1, index / max)

    @property
    def remaining(self):
        return max(max - index, 0)

    def start(self):
        update()

    def goto(self, index):
        incr = index - index
        next(incr)

    def iter(self, it):
        try:
            max = len(it)
        except TypeError:
            pass

        with self:
            for x in it:
                yield x
                next()
